    private void MenuItemLogOutActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_MenuItemLogOutActionPerformed
        int response = JOptionPane.showConfirmDialog(this, "Apakah Anda ingin Log Out?", "Confirm", JOptionPane.YES_NO_OPTION, JOptionPane.QUESTION_MESSAGE);
        //`->menampilkan pop up pertanyaan Apakah Anda ingin Log Out?
            if(response==JOptionPane.YES_OPTION){ //Jika memilih yes
                new Login().setVisible(true); //method yang membuat JFrame Login muncul di layar
                this.dispose(); //menutup JFrame Dashboard
            } else if(response==JOptionPane.NO_OPTION){ //Jika memilih no
                //tidak terjadi apa-apa
            } else if(response==JOptionPane.CLOSED_OPTION){ //Jika memilih closed
                //tidak terjadi apa-apa
            }
    }//GEN-LAST:event_MenuItemLogOutActionPerformed

